mapper.select:
select a.tag_text,b.* from tab_tag_info a,(select m.tid,count(*) zs from tab_content_tag m,tab_content_info n where m.cid=n.cid and n.c_status in (0,3,4) group by m.tid ) b where a.tid=b.tid
AND a.tag_text like '%${tagText}%'
order by b.zs desc limit #{pageStart},#{pageSize}

SELECT a.tid,FROM_UNIXTIME(SUBSTR(a.create_time,1,LENGTH(a.create_time)-3)) createTime,b.tag_text from tab_tag_hot a,tab_tag_info b where a.tid=b.tid and a.tag_type=1
AND b.tag_text like '%${tagText}%'
order by a.create_time desc limit #{pageStart},#{pageSize}

SELECT a.tid,FROM_UNIXTIME(SUBSTR(a.create_time,1,LENGTH(a.create_time)-3)) createTime,a.image_url,a.order_num,b.tag_text from tab_tag_hot a,tab_tag_info b where a.tid=b.tid and a.tag_type=2
AND b.tag_text like '%${tagText}%'
order by a.create_time desc limit #{pageStart},#{pageSize}
